mapper.update:
update cems.com_entrust
set ent_state = #{status}
where id = #{id}

update cems.com_entrust
set ent_plan = #{entPlan}
where id = #{id}

UPDATE
    com_user_info
SET user_money = #{userMoney}
where id = #{id}

mapper.select:
SELECT ent_type
FROM com_entrust_type
ORDER BY com_entrust_type.id;

select id, ent_type
from com_entrust_type
where id = #{id}

mapper.update:
update com_entrust
set ent_agent = #{userId}
  , ent_plan='已被接'
where id = #{taskId}

mapper.select:
select id,
ent_data,
ent_consignor,
ent_agent,
ent_state,
ent_type_id,
ent_plan,
gmt_create,
gmt_end,
ent_money
from com_entrust
where ent_state = '已审核'

    and ent_plan = #{entPlan}


    and ent_type_id = #{textType}


    and (
    ent_data like #{text}
    or gmt_create like #{text}
    or gmt_end like #{text}
    )

select id, ent_type
from com_entrust_type
where ent_type = #{text}

select id, ent_type
from com_entrust_type
where id = #{id}

select id,
ent_data,
ent_consignor,
ent_agent,
ent_state,
ent_type_id,
ent_plan,
gmt_create,
gmt_end,
ent_money
from com_entrust
where ent_state = '已审核'
and ent_agent = #{id}

    and ent_plan = #{entPlan}


    and ent_type_id = #{textType}


    and (
    ent_data like #{text}
    or gmt_create like #{text}
    or gmt_end like #{text}
    )

select id,
ent_data,
ent_consignor,
ent_agent,
ent_state,
ent_type_id,
ent_plan,
gmt_create,
gmt_end,
ent_money
from com_entrust
where ent_state = '已审核'
and ent_consignor = #{id}

    and ent_plan = #{entPlan}


    and ent_type_id = #{textType}


    and (
    ent_data like #{text}
    or gmt_create like #{text}
    or gmt_end like #{text}
    )
