mapper.select:
select id, comm_re_data, comm_user, comm_re_user, comm_reply, gmt_create
from comment_reply
where comm_reply = #{id}

select id, comm_re_data, comm_user, comm_re_user, comm_reply, gmt_create
from comment_reply
where comm_reply = #{id}
  and (comm_re_data like #{text}
    or comm_re_user like #{text}
    or gmt_create like #{text})
