mapper.select:
select id, user_phone, user_pwd, user_role, status
from cems.com_user
where user_phone like #{phone}
  and status = #{status}

select id, user_phone, user_pwd, user_role, status
from com_user
where user_phone = #{phone}
  and user_pwd = #{psw}

select id, user_phone, user_pwd, user_role, status
from com_user
where user_phone = #{num}

select user_id,
       user_email
from com_user_info
where user_email = #{emil}
  and user_id = #{id}

